styles.css:
a{
   text-decoration: none;
}
body{
    overflow-x: hidden;
}
.default-first-block, .default-footer{
   margin-block-start: 0px;
}
.box-home-services{
    min-width: 250px !important;
height: 800px !important;
}
.imgs-home-services img{
            max-width: max-content
 !important;
    }
.logo-footer img{
   object-fit: contain !important;
}

.links-footer > .has-child > .wp-block-navigation__submenu-container{
  display: none !important;
}

.marquees{
  cursor: pointer !important;
  transition: .7s all !important;
flex: 1 !important;
}
.marquees > .wp-block-cover__background.has-background-dim.has-background-dim-40{
  transition: .7s all !important;
}
.marquees:hover > .wp-block-cover__background.has-background-dim.has-background-dim-40{
  opacity: 0 !important;
}
.marquees:hover > .wp-block-cover__image-background{
  filter: none !important;
}
.marquees > .wp-block-cover__image-background{
  transition: .7s all !important;
}

::-webkit-scrollbar {
    width: 10px;
  }

::-webkit-scrollbar-track {
    background: var(--wp--preset--color--custom-color-3);
}

::-webkit-scrollbar-thumb {
    background: var(--wp--preset--color--custom-color-2);
    border-radius: 20px;
}

@media screen and (max-width: 768px) {
.fade-up, .fade-left, .fade-right{
    opacity: 1;
    transform: translateX(0), translateY(0);
}
    .containers-home-start{
        padding: 0 !important;
    }
    .texts-home-start{
        flex-basis: 90vw !important;
    }
.btn-contact{
   display: none !important;
}
    .section-home-blog{
        grid-template-columns: repeat(1, minmax(0, 1fr)) !important;
    }
    .imgs-home-blog{
        height: auto !important;
        width: 80vw !important;
    }
    .imgs-home-services{
        width: 90px !important;
    }
      .imgs-home-services img{
    width: 90px !important;
  }
  .container-imgs-home-services {
    flex-direction: column !important;
  }
  .box-home-services {
    min-width: 200px !important;
    height: auto !important;
  }
    .section-home-services{
        flex-wrap: wrap !important;
    }
  .marquee > .container-marquees{
    flex-direction: column !important;
    align-items: stretch !important;
  }
  .marquee-alternative{
    flex-direction: column !important;
    align-items: stretch !important;
  }
    .menu{
        padding-right: 1rem !important;
        padding-left: 1rem !important;
    }
    .title-home{
        font-size: 50px !important;
    }
.title-destination{
        flex-direction: column !important;
    }
.container-blog{
        padding-right: 1rem 
 !important;
        padding-left: 1rem 
 !important;
    }
    .imgs-blog{
        width: 80vw 
 !important;
    }
.container-texts-blog{
        max-width: 80vw !important;
    }
    .big-titles{
        font-size: 35px !important;
    }

}
@media screen and (max-width: 450px) {
    .container-blog {
        padding-right: 0.5rem !important;
        padding-left: 0.5rem !important;
      }
}
:root{--wp--style--global--content-size:100%;--wp--style--global--wide-size:1000px}body{background-color:#fff!important}
